import React, { Component } from "react";
const millify = require("millify");

export default class Count extends Component {
  state = {
    hasErrors: false,
    organisation: this.props.organisation,
    repo: this.props.repo,
    count: 0,
  };

  componentDidMount() {
    fetch(
      "https://github-dependents.glitch.me/" +
        this.state.organisation +
        "/" +
        this.state.repo +
        "?limit=1"
    )
      .then(res => res.json())
      .then(res => this.setState({ count: res.totalDependants }))
      .catch(() => this.setState({ hasErrors: true }));
  }

  render() {
    const totalCount = this.state.count;
    const repoName = this.state.repo;
    const millied = millify(totalCount);

    return (
      <div>
        {repoName} is used by <br />
        {totalCount} repos <br /> <br />
      </div>
    );
  }
}
